mapper.select:
select * from ( select ROW_NUMBER() over (order by alert_num desc) as rn, alert_num, alert_contents from alert
alert_contents like '%' || #{searchWord} ||'%' or alert_num like '%' || #{searchWord} || '%'
)sub1 where rn between #{startRow} and #{endRow}

mapper.delete:
BEGIN
-- alert_list 테이블에서 alert_num 삭제
delete from alert_list
where alert_num in

    #{alertNum}
;

-- alert 테이블에서 alert_num 삭제
delete from alert
where alert_num in

    #{alertNum}
;
END;
